import Additem from "./Additem";
import "./App.css";
import Content from "./Content";
import Footer from "./Footer";
import Header from "./Header";
import { useState } from "react";
function App() {
  const [items, setItems] = useState([
    { id: 1, checked: true, msg: "I love Coding" },
    { id: 2, checked: false, msg: "Learnig React.." },
    { id: 3, checked: true, msg: "Hello World" },
  ]);

  const handleCheck = (id) => {
    const listItems = items.map((item) =>
      item.id === id ? { ...item, checked: !item.checked } : item
    );
    setItems(listItems);
    localStorage.setItem("to-do list", JSON.stringify(listItems));
  };
  const handleDelete = (id) => {
    const listItems = items.filter((item) => item.id !== id);
    setItems(listItems);
    localStorage.setItem("to-do list", JSON.stringify(listItems));
  };

  return (
    <div className="App shadow rounded bg-grey">
      <Header tittle="To_Do List " length={items.length} />
      <Additem />
      <Content
        items={items}
        handleCheck={handleCheck}
        handleDelete={handleDelete}
      />
      <Footer />
    </div>
  );
}

export default App;
