import Additem from "./Additem";
import "./App.css";
import Content from "./Content";
import Footer from "./Footer";
import Header from "./Header";
import { useState } from "react";
import Search from "./Search";
function App() {
  const [items, setItems] = useState([
    { id: 1, checked: true, msg: "I love Coding" },
    { id: 2, checked: false, msg: "Learnig React.." },
    { id: 3, checked: true, msg: "Hello World" },
  ]);
  const [newItem, setNewItem] = useState("");
  const addItem = (item) => {
    const id = items.length ? items[items.length - 1].id + 1 : 1;
    const addNewItem = { id, checked: false, item };
    const listItems = [...(items + addNewItem)];
    setItems(listItems);
localStorage.setItem



  };
  const handleCheck = (id) => {
    const listItems = items.map((item) =>
      item.id === id ? { ...item, checked: !item.checked } : item
    );
    setItems(listItems);
    localStorage.setItem("to-do list", JSON.stringify(listItems));
  };
  const handleDelete = (id) => {
    const listItems = items.filter((item) => item.id !== id);
    setItems(listItems);
    localStorage.setItem("to-do list", JSON.stringify(listItems));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newItem) return;
    console.log(newItem);
    setNewItem("");
  };

  return (
    <div className="App shadow rounded ">
      <Header tittle="To_Do List " length={items.length} />
      <Additem
        newItem={newItem}
        setNewItem={setNewItem}
        handleSubmit={handleSubmit}
      />
      <Search />
      <Content
        items={items}
        handleCheck={handleCheck}
        handleDelete={handleDelete}
      />
      <Footer />
    </div>
  );
}

export default App;
